<body ng-controller="BTorrentCtrl" class="ng-scope">
<header>
    <h1>βTorrent<span class="version ng-binding"> v0.15</span></h1>
    <div class="views" ng-show="$root.client.torrents.length == 0"><a ng-href="/#" href="https://btorrent.xyz/#">Full</a> | <a ng-href="/download" href="https://btorrent.xyz/download">Single Download</a> | <a ng-href="/view" href="https://btorrent.xyz/view">Stream / View</a></div>
</header>
<div id="viewer" ng-style="$root.viewerStyle"></div>
<!-- ngView:  --><div id="view"  class="ng-scope">
    <div class="container ng-scope">
        <div class="row">
            <div class="four columns">
                <input class="u-full-width ng-pristine ng-untouched ng-valid ng-empty" type="text" placeholder="magnet, hash or http(s) .torrent" ng-model="torrentInput" ng-disabled="$root.disabled">
            </div>
            <div class="two columns download-button">
                <button ng-click="addMagnet()" ng-disabled="!torrentInput.length || $root.disabled" ng-class="{&#39;button-primary&#39;: torrentInput.length}" disabled="disabled"><i class="fa fa-download"></i> Download</button>
            </div>
            <div class="three columns">
                <button type="file" ngf-select="$root.openTorrentFile($file)" ng-disabled="$root.disabled" ng-class="{&#39;button-primary&#39;: !$root.disabled}" class="button-primary"><i class="fa fa-folder-open"></i> Open torrent file</button>
            </div>
            <div class="three columns u-pull-right">
                <button class="u-pull-right button-primary seedFiles" multiple="multiple"  ng-class="{&#39;button-primary&#39;: !$root.disabled}">
                    <i class="fa fa-upload"></i> Seed files</button>

                <form name="upload">
                    <label for="torrentId">Download from a magnet link: </label>
                    <input type="file" name="myfile">
                </form>

                <h2>Log</h2>
                <div class="log"></div>
            </div>
